import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { collection, addDoc, getDocs, query, where } from 'firebase/firestore';
import { db } from './firebase/firebase.config';

function App() {
  const [visits, setVisits] = useState(null);
  const [loaded, setLoaded] = useState(false);

  const fetchVisits = async () => {
    try {
      const q = query(collection(db, 'visits'));
      const querySnapshot = await getDocs(q);
      setVisits(querySnapshot.size);
      setLoaded(true);
    } catch (error) {
      console.log('Error fetching visits', error);
    }
  };

  const updateVisits = async () => {
    try {
      await addDoc(collection(db, 'visits'), { timestamp: new Date() });
      setVisits((prevVisits) => prevVisits + 1);
    } catch (error) {
      console.log('Error updating visits', error);
    }
  };

  useEffect(() => {
    if (!loaded) {
      fetchVisits();
    }
  }, [loaded]);

  useEffect(() => {
    if (visits !== null) {
      updateVisits();
    }
  }, [visits]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          You are visitor number {visits === null ? 'loading...' : visits}
        </p>
      </header>
    </div>
  );
}

export default App;
