import React, { useEffect, useState } from 'react'
import logo from './logo.svg';
import './App.css';
import { collection, doc, setDoc, getDoc, getDocs, where, query } from 'firebase/firestore'
import { db, auth, user } from './firebase/firebase.config'

function App() {
  const [visits, setVisit] = useState(null)
  const [loaded, setLoaded] = useState(false)


  const fetchVisits = async () => {
    const visitsRef2 = doc(db, 'traffic', "site-visits")
    const docSnap = await getDoc(visitsRef2)
    if (docSnap.exists()) {
      setVisit(docSnap.data())
      setLoaded(true)
    }
  }

  const updateVisits = async () => {
    const visitsRef = doc(db, "traffic", "site-visits")
    await setDoc(visitsRef, {
      total: visits.total + 1,
    })
  }


  useEffect(() => {
    if (!loaded) {
      fetchVisits()
    }
  }, [loaded])


  useEffect(() => {
    if (visits !== null) {
      updateVisits()
    }
  }, [visits])


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
