import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { collection, doc, setDoc, getDoc, getDocs, where, query } from 'firebase/firestore';
import { db, auth, user } from './firebase/firebase.config';

function App() {
  const [visits, setVisits] = useState(null);
  const [loaded, setLoaded] = useState(false);

  const fetchVisits = async () => {
    try {
      const visitsRef = doc(db, 'visits', 'site-visits');
      const docSnap = await getDoc(visitsRef);
      if (docSnap.exists()) {
        setVisits(docSnap.data());
        setLoaded(true);
      } else {
        // Create a new 'site-visits' document with a total value of 0
        await setDoc(visitsRef, { total: 0 });
        setVisits({ total: 0 });
        setLoaded(true);
      }
    } catch (error) {
      console.log('Error fetching visits', error);
    }
  };

  const updateVisits = async () => {
    try {
      const visitsRef = doc(db, 'visits', 'site-visits');
      await setDoc(visitsRef, {
        total: visits.total + 1,
      });
      setVisits({ total: visits.total + 1 });
    } catch (error) {
      console.log('Error updating visits', error);
    }
  };

  useEffect(() => {
    if (!loaded) {
      fetchVisits();
    }
  }, [loaded]);

  useEffect(() => {
    if (visits !== null) {
      updateVisits();
    }
  }, [visits]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer">
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
