import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import { collection, doc, setDoc, getDoc, getDocs, where, query } from 'firebase/firestore';
import { db, auth, user } from './firebase/firebase.config';
import axios from 'axios';


function App() {
  let currentDate = new Date().toISOString().slice(0, 10)
  const [visits, setVisit] = useState(null);
  const [loaded, setLoaded] = useState(false);
  const [userIP, setUserIP] = useState(null);
  const [userData, setUserData] = useState(null);


  // Fetch user's IP address and add to state
  const fetchIP = async () => {
    const res = await axios.get('https://geolocation-db.com/json/')
    setUserIP(res.data.IPv4)
  }



  const fetchVisits = async () => {
    try {
      const trafficRef = collection(db, 'traffic');
      const visitsRef = doc(trafficRef, 'site-visits');
      const docSnap = await getDoc(visitsRef);
      if (docSnap.exists()) {
        setVisit(docSnap.data());
      } else {
        await setDoc(visitsRef, { total: 0 });
        setVisit({ total: 0 });
      }
      setLoaded(true);
    } catch (error) {
      console.log('Error fetching visits:', error);
    }
  };

  const updateVisits = async () => {
    try {
      const trafficRef = collection(db, 'traffic');
      const visitsRef = doc(trafficRef, 'site-visits');
      await setDoc(visitsRef, { total: visits.total + 1 }, { merge: true });
    } catch (error) {
      console.log('Error updating visits:', error);
    }
  };

  useEffect(() => {
    if (!loaded) {
      fetchVisits();
    }
  }, [loaded]);

  useEffect(() => {
    if (visits !== null) {
      updateVisits();
    }
  }, [visits]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>Edit <code>src/App.js</code> and save to reload.</p>
        <a className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer">
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
